#!/usr/bin/env node
// PURPOSE: Project health monitoring following disciplined development principles

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

class HealthMonitor {
  constructor() {
    this.maxFileSize = 300; // lines
    this.maxImports = 3;
    this.results = {
      oversized: [],
      tooManyImports: [],
      missingPurpose: [],
      totalFiles: 0,
      healthScore: 100
    };
  }

  checkProject() {
    console.log('ðŸ” Running {{PROJECT_NAME}} health check...');

    this.scanDirectory('src');
    this.generateReport();

    return this.results.healthScore >= 80;
  }

  scanDirectory(dir) {
    if (!fs.existsSync(dir)) return;

    const files = fs.readdirSync(dir);

    files.forEach(file => {
      const filePath = path.join(dir, file);
      const stat = fs.statSync(filePath);

      if (stat.isDirectory()) {
        this.scanDirectory(filePath);
      } else if (this.isCodeFile(file)) {
        this.checkFile(filePath);
      }
    });
  }

  isCodeFile(filename) {
    return /\.(js|ts|jsx|tsx|py|java|go|rs)$/.test(filename);
  }

  checkFile(filePath) {
    this.results.totalFiles++;

    const content = fs.readFileSync(filePath, 'utf8');
    const lines = content.split('\n');

    // Check file size
    if (lines.length > this.maxFileSize) {
      this.results.oversized.push({
        path: filePath,
        lines: lines.length,
        excess: lines.length - this.maxFileSize
      });
    }

    // Check import count
    const imports = this.countImports(content);
    if (imports > this.maxImports) {
      this.results.tooManyImports.push({
        path: filePath,
        imports: imports,
        excess: imports - this.maxImports
      });
    }

    // Check for PURPOSE comment
    if (!content.includes('PURPOSE:')) {
      this.results.missingPurpose.push(filePath);
    }
  }

  countImports(content) {
    const importPatterns = [
      /^import .+ from .+$/gm,
      /^const .+ = require\(.+\)$/gm,
      /^from .+ import .+$/gm
    ];

    return importPatterns.reduce((count, pattern) => {
      const matches = content.match(pattern);
      return count + (matches ? matches.length : 0);
    }, 0);
  }

  generateReport() {
    const issues = this.results.oversized.length + 
                  this.results.tooManyImports.length + 
                  this.results.missingPurpose.length;

    this.results.healthScore = Math.max(0, 100 - (issues * 10));

    console.log('\nðŸ“Š Health Report:');
    console.log(`Files scanned: ${this.results.totalFiles}`);
    console.log(`Health score: ${this.results.healthScore}%`);

    if (this.results.oversized.length > 0) {
      console.log('\nðŸš¨ Oversized files:');
      this.results.oversized.forEach(file => {
        console.log(`  ${file.path}: ${file.lines} lines (+${file.excess})`);
      });
    }

    if (this.results.tooManyImports.length > 0) {
      console.log('\nâš ï¸ Too many imports:');
      this.results.tooManyImports.forEach(file => {
        console.log(`  ${file.path}: ${file.imports} imports (+${file.excess})`);
      });
    }

    if (this.results.missingPurpose.length > 0) {
      console.log('\nðŸ“ Missing PURPOSE comments:');
      this.results.missingPurpose.forEach(file => {
        console.log(`  ${file}`);
      });
    }
  }
}

if (require.main === module) {
  const monitor = new HealthMonitor();
  const healthy = monitor.checkProject();
  process.exit(healthy ? 0 : 1);
}

module.exports = HealthMonitor;

{
  "scripts": {
    "check:health": "node scripts/health-check.js",
    "check:size": "find src -name '*.js' -o -name '*.ts' -o -name '*.jsx' -o -name '*.tsx' | xargs wc -l | sort -nr",
    "session:start": "echo 'ðŸš€ Session started at' && date && echo '{{SESSION_OBJECTIVE}}'",
    "session:end": "npm run check:health && echo 'âœ… Session ended at' && date"
  }
}
